binding.script:
var which = getprop("instrumentation/pfd[0]/nav-src") or getprop("instrumentation/pfd[0]/preview") or 0;
if (which > 0) {
var degrees = math.round(getprop("instrumentation/nav[" ~ (which - 1) ~ "]/heading-deg"));
setprop("instrumentation/nav[" ~ (which - 1) ~ "]/radials/selected-deg", degrees);
}

var which = getprop("instrumentation/pfd[1]/nav-src") or getprop("instrumentation/pfd[1]/preview") or 0;
if (which > 0) {
var degrees = math.round(getprop("instrumentation/nav[" ~ (which - 1) ~ "]/heading-deg"));
setprop("instrumentation/nav[" ~ (which - 1) ~ "]/radials/selected-deg", degrees);
}
